package Koohinoor;import akka.actor.{Actor, ActorSystem, Props};/**  *  AKKA Actor model Example  */class HelloActor(name : String ) extends Actor{  override def receive: Receive = {    case "Hello" => println("Hello from %s".format(name));    case _ => println("Sorry World");  };};object Main extends App {  val actorSystem = ActorSystem("Hey");  val helloActor = actorSystem.actorOf(Props(new HelloActor("Ali")), name = "hello");  helloActor ! "Hello";}